import React, { Component } from "react";

export default class Search extends Component {
  handleSearch = () => {
    //连续解构赋值重命名
    const {keyInput: { value: keyWord }} = this;
    this.props.updateState({isLoading:true,isFirst:false})
    //调用api接口
    this.api.getUsers(keyWord).then(res => {
      const stateObj = {userList: res, isLoading: false, isFirst: false}
       this.props.updateState(stateObj)
    },
    error =>{
      const stateObj = {err: error}
      this.props.updateState(stateObj)
    })

  };
  render() {
    return (
      <section className="jumbotron">
        <h3 className="jumbotron-heading">Search Github Users</h3>
        <div>
          <input
            ref={(c) => {
              this.keyInput = c;
            }}
            type="text"
            placeholder="enter the name you search"
          />
          &nbsp;
          <button onClick={this.handleSearch}>Search</button>
        </div>
      </section>
    );
  }
}
